	/**
	 * Returns the complete translations map for a given language identified by language tag.
	 *
	 * @param   string|null $lang  Will fall back to current application language.
	 *
	 * @return  array
	 */
	public static function getLanguage(string $lang = null) : array
	{
		$lang = (!is_null($lang) ? $lang : Factory::getConfig()->get('app_language'));

		return ArrayHelper::getValue(static::$strings, $lang, [], 'ARRAY');
	}

	/**
	 * Translates a given text key into its mapped value.
	 *
	 * @param   string $key
	 * @param   string|empty $lang  Will fall back to current application language.
	 *
	 * @return  string|null
	 */
	public static function translate(string $key, string $lang = '') :? string
	{
		/* utility code to find lang keys missing in either language section. Just uncomment and reload page and find a list of missing translations.
		$strings = static::$strings;
		$de = $strings['de'];
		$en = $strings['en'];
		$diff = (count($de) > count($en) ? array_diff_key($de, $en) : array_diff_key($en, $de));
		*/

		$lang = (empty(trim($lang)) ? Factory::getConfig()->get('app_language') : $lang);

		// Attempt to get the requested language collection.
		$collection = static::getLanguage($lang);

		// Check whether a collection could be fetched or fall back to App default if not.
		$lang = (count($collection) ? $lang : Factory::getConfig()->get('app_language'));

		// Fetch collection for fallback language.
		$collection = static::getLanguage($lang);

		// Translate the language key.
		$value      = ArrayHelper::getValue($collection, $key, $key, 'STRING');

		// Free memory.
		unset($collection);

		return $value;
	}

	/**
	 * Converts German Umlaut to British equivalent.
	 *
	 * @param   string  $string  The string to be handled.
	 *
	 * @return  string  The handled string.
	 */
	public static function translateGermanUmlauts(string $string) : string
	{
		$string = preg_replace('/Ä/', 'Ae', $string);
		$string = preg_replace('/ä/', 'ae', $string);
		$string = preg_replace('/Ö/', 'Oe', $string);
		$string = preg_replace('/ö/', 'oe', $string);
		$string = preg_replace('/Ü/', 'Ue', $string);
		$string = preg_replace('/ü/', 'ue', $string);

		return $string;
	}

	/**
	 * Reverts translation for a given text value.
	 *
	 * @param   string $string
	 * @param   string|empty $lang  Will fall back to current application language.
	 * @param   false  $caseInsensitive
	 *
	 * @return  string|null
	 */
	public static function untranslate(string $string, string $lang = '', bool $caseInsensitive = false) :? string
	{
		$res  = null;

		$lang = (empty(trim($lang)) ? Factory::getConfig()->get('app_language') : $lang);

		if (!array_key_exists($lang, static::$strings))
		{
			throw new RuntimeException(sprintf('Unregistered language <strong><em>""</em></strong. Translation is not available.', mb_strtoupper($lang)));
		}

		try
		{
			array_walk(static::$strings[$lang], function ($val, $key) use (&$string, &$res, &$caseInsensitive)
			{
				if ($caseInsensitive)
				{
					if (mb_strtolower($val) === mb_strtolower($string))
					{
						$res = $key;

						// break out of loop (the only way how) (see: https://stackoverflow.com/a/17853359)
						throw new Exception;
					}
				}
				else
				{
					if ($val === $string)
					{
						$res = $key;

						// break out of loop (the only way how) (see: https://stackoverflow.com/a/17853359)
						throw new Exception;
					}
				}
			});
		}
		catch (Exception $e)
		{
			// do nothing
		}

		return (is_null($res)) ? $string : $res;
	}

	private static function getDiff(string $tagA, string $tagB) : array
	{
		$diff = [];

		$arrA = static::getLanguage(mb_strtolower($tagA));
		$arrB = static::getLanguage(mb_strtolower($tagB));

		return (count($arrA) > count($arrB)) ? array_diff_key($arrA, $arrB) :  array_diff_key($arrB, $arrA);
	}
}
